const uploadToWordPress = async () => {
    const pluginName = 'custom-plugin.php';
    const blob = new Blob([code], { type: 'text/php' });

    const formData = new FormData();
    formData.append('file', blob, pluginName);

    const username = 'admin'; // üëà your WP admin user
    const password = 'app-password-here'; // üëà create an Application Password from WP admin
    const siteUrl = 'http://woo-plugin-test.local'; // üëà your LocalWP site URL

    const auth = btoa(`${username}:${password}`);

    try {
        const response = await fetch(`${siteUrl}/wp-json/wp/v2/plugins`, {
            method: 'POST',
            headers: {
                Authorization: `Basic ${auth}`
            },
            body: formData
        });

        if (response.ok) {
            alert('‚úÖ Plugin uploaded and activated!');
        } else {
            const err = await response.text();
            alert('‚ùå Failed: ' + err);
        }
    } catch (err) {
        console.error(err);
        alert('‚ùå Error uploading plugin.');
    }
};
