import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import './App.css';

export default function App() {
    const [prompt, setPrompt] = useState('');
    const [code, setCode] = useState('');
    const [loading, setLoading] = useState(false);

    const generatePlugin = async () => {
        setLoading(true);
        const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

        try {
            const res = await fetch(`${API_BASE_URL}/api/generate`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ prompt }),
            });

            if (!res.ok) {
                const errorData = await res.json();
                alert(`❌ Plugin generation failed:\n${errorData.details || errorData.error}`);
                setLoading(false);
                return;
            }

            const data = await res.json();
            setCode(data.code);
        } catch (err) {
            alert(`❌ Error connecting to backend:\n${err}`);
        }

        setLoading(false);
    };


    const downloadPlugin = () => {
        const blob = new Blob([code], { type: 'text/php' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'custom-plugin.php';
        a.click();
        URL.revokeObjectURL(url);
    };

    return (
        <div className="app">
            <h1>Woo Plugin Generator</h1>
            <textarea
                placeholder="Describe what you want the plugin to do..."
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
            />
            <button onClick={generatePlugin} disabled={loading}>
                {loading ? 'Generating...' : 'Generate Plugin'}
            </button>
            <Editor
                height="400px"
                defaultLanguage="php"
                value={code}
                onChange={(value) => setCode(value ?? '')}
                theme="vs-dark"
            />
            {code && (
                <button onClick={downloadPlugin}>Download Plugin (.php)</button>
            )}
        </div>
    );
}
